	li $s7, 0x15ff00 # Temp player colour
	 
	li playerX, 10
	li playerY, 20
	
	move $a0, $s1
	
	jal fillScrn
	
	li $t7, 0
mkPltLoop:
	bgt $t7, PLATFORMBYTE, mkPltLoopEnd
	
	li $a0, 128
	jal genRan				
	move $t0, $v0	#X pos
	addi $t0, $t0, 1
	
	li $a0, 64
	jal genRan
	move $t1, $v0	#Y pos
	addi $t1, $t1, 1
	
	li $a0, 3
	jal genRan
	andi $v0, $v0, 1
	move $t2, $v0	#Current Dir
	
	li $a0, 4
	jal genRan
	move $t3, $v0	#Movement Type
	
	li $a0, 16
	jal genRan
	move $t4, $v0	#Length
	addi $t4, $t4, 1
	
	addi $sp, $sp, 4
	sw $t0, 0($sp)
	addi $sp, $sp, 4
	sw $t1, 0($sp)
	addi $sp, $sp, 4
	sw $t2, 0($sp)
	addi $sp, $sp, 4
	sw $t3, 0($sp)
	addi $sp, $sp, 4
	sw $t4, 0($sp)
	
	jal mkPlt
	move $t0, $v0
	
	#Randomize offsets
	li $a0, 13
	jal genRan
	move $t4, $v0	
	addi $t4, $t4, 1
	sll $t4, $t4, 12
	add $t0, $t0, $t4
	
	li $a0, 13
	jal genRan
	move $t4, $v0	
	addi $t4, $t4, 1
	sll $t4, $t4, 8
	add $t0, $t0, $t4
	
	#Store in platforms array
	la $t6, platforms
	add $t6, $t6, $t7
	sw $t0, 0($t6)
	
	addi $t7, $t7, 4
	move $a0, $t0
	
	move $a1, $s2
	jal drwPlt
	j mkPltLoop
mkPltLoopEnd:
MAIN:	
	li $t8, 0
	la $t9, platforms
drwPltLoop:
	bgt $t8, PLATFORMBYTE, drwPltLoopEnd
	lw $a0, 0($t9)
	jal updtPlt
	#jal mvPltR
	sw $v0, 0($t9)
	add $t8, $t8, 4
	add $t9, $t9, 4
	j drwPltLoop
drwPltLoopEnd:
	jal updatePlayer
	li $v0, 32
	li $a0, TIMING
	syscall
	move $a0, $t7
	j MAIN
	
		#CLEARS ALL REGISTERS
checkBound:	#Checks for platform existence at position (playerX+$a0, playerY+$a1) and returns 0 or 1 ($a0 will hold the platform that was collided with)
	move $t9 $ra
	add $t0, $a0, playerX
	add $t1, $a1, playerY
	la $t2, platforms
	add $t3, $t2, PLATFORMBYTE
	#Loop through every platform
boundPltLoop:
	bgt $t2, $t3, boundPltEnd
	lw $a0, 0($t2)
	jal pltY
	move $t4, $v0
	jal pltFY
	li $t6, PLATMV
	mult $v0, $t6
	mflo $v0
	add $t4, $t4, $v0
	sub $t4, $t1, $t4
	bnez $t4, boundPltXLoopEnd
	
	jal pltX
	move $t4, $v0
	jal pltFX
	li $t6, PLATMV
	mult $v0, $t6
	mflo $v0
	add $t4, $t4, $v0
	jal pltLen
	li $t7, PLATMV
	mult $v0, $t7
	mflo $v0
	add $v0, $v0, $t4
	#Loop through every x value of length from platform
boundPltXLoop:
	bgt $t4, $v0, boundPltXLoopEnd
	sub $t6, $t0, $t4
	beqz $t6, yesColl
	
	add $t4, $t4, 1
	j boundPltXLoop
boundPltXLoopEnd:
	addi $t2, $t2, 4
	j boundPltLoop
yesColl:	
	li $v0, 1
	j boundReturn
boundPltEnd:
	li $v0, 0
boundReturn:
	jr $t9
	
updatePlayer:
	move $t5, $ra
	
	jal clearPlayer
	
	li $a0, 1
	li $a1, 1
	jal checkBound	
	
	beqz $v0, dontTrackPlt
	jal pltDir
	move $t2, $v0
	jal pltMv
	li $t0, PLATHOR
	beq $v0, $t0 pltH
	
	li $t0, PLATVERT
	beq $v0, $t0, pltV
	
	li $t0, PLATUPL
	beq $v0, $t0, pltUL
	
	li $t0, PLATUPR
	beq $v0, $t0, pltUR
pltH:
	beqz $t2, movePlyrLft
	add playerX, playerX, PLATMV
	j dontTrackPlt
movePlyrLft:
	sub playerX, playerX, PLATMV
	j dontTrackPlt
pltV:
	beqz $t2, movePlyrDwn
	sub playerY, playerY, PLATMV
	j dontTrackPlt
movePlyrDwn:
	add playerY, playerY, PLATMV
	j dontTrackPlt
pltUR:
pltUL:
dontTrackPlt:	
	li $t1, KEYBOARD
	lw $t0, 0($t1)
	beqz $t0, keyCaseEnd
	lw $t0, 4($t1)
	beq $t0, 32, keyCaseSpace
	beq $t0, 97, keyCaseA
	beq $t0, 65, keyCaseA
	beq $t0, 68, keyCaseD
	beq $t0, 100, keyCaseD
	beq $t0, 83, keyCaseS
	beq $t0, 115, keyCaseS
	beq $t0, 119, keyCaseW
	beq $t0, 87, keyCaseW
	j NoMove
keyCaseA:
	sub playerX, playerX, 1
	j keyCaseEnd
keyCaseD:
	add playerX, playerX, 1
	j keyCaseEnd
keyCaseW:
	sub playerY, playerY, 1
	j keyCaseEnd
keyCaseS:
	add playerY, playerY, 1
	j keyCaseEnd
keyCaseSpace:
	j keyCaseEnd
keyCaseEnd:
	jal drawPlayer
NoMove:
	jr $t5
	
clearPlayer:
	move $t4, $ra
	move $t2, playerY
	li $t1, LENGTH
	mult $t2, $t1
	mflo $t2
	li $t1, 4
	mult playerX, $t1
	mflo $t1
	add $t3, $t2, $t1
	
	move $a0, $t3
	li $a1, 16
	jal replaceCol
	
	sub $t3, $t3, $s3
	move $a0, $t3
	li $a1, 16
	jal replaceCol
	
	jr $t4
	
drawPlayer:#Draws player at specified coordinates in x,y
	move $t4, $ra
	move $t2, playerY
	li $t1, LENGTH
	mult $t2, $t1
	mflo $t2
	li $t1, 4
	mult playerX, $t1
	mflo $t1
	add $t2, $t2, $t1
	add $t2, $t2, $s0
	
	move $a0, $t2
	li $a1, 16
	move $a2, $s7
	jal fill
	
	sub $t2, $t2, $s3
	move $a0, $t2
	li $a1, 16
	move $a2, $s7
	jal fill
	
	jr $t4
	
updtPlt:#Updates platfrom position, and does partial redraws
	move $s4, $ra
	jal pltMv
	move $t1 $v0
	
	li $t0, PLATHOR
	beq $t1, $t0 updtH
	
	li $t0, PLATVERT
	beq $t1, $t0, updtV
	
	li $t0, PLATUPL
	beq $t1, $t0, updtUL
	
	li $t0, PLATUPR
	beq $t1, $t0, updtUR
updtH:
	jal pltFX
	move $t0, $v0
	li $t1, 15
	beqz $t0, updtFlipH
	beq $t0, $t1 updtFlipH
	j updtHPos
updtFlipH:
	li $t1, 128		#Get mask for dir flag
	xor $a0, $a0, $t1
updtHPos:
	li $t1, 128		#Get mask for dir flag
	and $t1, $a0, $t1	#Extract dir flag 
	beqz $t1, updtL
	#Going right
	jal mvPltR
	j updtEND
updtL:	#Going left
	jal mvPltL
	j updtEND
updtV:
	jal pltFY
	move $t0, $v0
	li $t1, 15
	beqz $t0, updtFlipV
	beq $t0, $t1 updtFlipV
	j updtVPos
updtFlipV:
	li $t1, 128		#Get mask for dir flag
	xor $a0, $a0, $t1
updtVPos:
	li $t1, 128		#Get mask for dir flag
	and $t1, $a0, $t1	#Extract dir flag 
	beqz $t1, updtD
	#Going up
	jal mvPltU
	j updtEND
updtD:	#Going Down
	jal mvPltD
	j updtEND
updtUL:
updtUR:
updtEND:
	move $v0, $a0
	jr $s4
	
genRan: #Generates a random number up to $a0
	li $v0, 42
	move $a1, $a0
	move $a0, $ra
	syscall
	move $v0, $a0
	jr $ra
	
replaceCol:	#Fills from $a0 to ($a0+$a1) with colour background
	add $t0, $a1, $a0
	la $t1, background
	add $t1, $t1, $a0
	
	add $a0, $a0, $s0
	add $t0, $t0, $s0
	
replaceColLoop:
	bgt $a0, $t0, replaceColLoopEnd
	lw $t2, 0($t1)
	sw $t2, 0($a0)
	#addi $a0, $a0, 32768
	addi $a0, $a0, 4
	addi $t1, $t1, 4
	j replaceColLoop
replaceColLoopEnd:
	jr $ra
		
drwPlt:	#Draws *entire* platform stored in a0, w/ colour stored in a1
	#Retrieves platform data
	#lw $a0, 0($sp)
	#addi $sp, $sp, -4
	
	#Stores original $ra
	move $t4, $ra
	
	#Calculate current x,y pos
	jal pltTX
	li $t1, 4
	mult $v0, $t1
	mflo $t3
	
	jal pltTY
	move $t1, $v0
	move $t0, $t3
	
	#initialize pixel cursor pos
	li $v0, LENGTH
	li $t2, BASE_ADDRESS
	mult $t1, $v0
	mflo $v0
	add $t0, $t0, $v0
	add $t0, $t0, $t2
	
	jal pltLen
	li $t3, PLATMV
	mult $v0, $t3
	mflo $v0
	li $t3, 4
	mult $v0, $t3
	mflo $v0
	
	move $t3, $a0
	
	move $a0, $t0
	move $a2, $a1
	move $a1, $v0
	
	jal fill
	
	move $v0, $t3
	jr $t4

fillScrn:#Fill whole screen 
	move $t0, $s0
	mult $s3, $s4
	mflo $t1
	div $t1, $t1 4
	add $t1, $t1, $s0
	la $t3, background
flScrnLoop:
	bgt $t0, $t1, flScrEnd
	lw $t4, 0($t3)
	sw $t4, 0($t0)
	addi $t0, $t0, 4
	addi $t3, $t3, 4
	j flScrnLoop
flScrEnd:
	jr $ra

fill:	#Fills from $a0 to ($a0+$a1) with colour $a2
	add $t0, $a1, $a0
fillLoop:
	bgt $a0, $t0, fillLoopEnd
	sw $a2, 0($a0)
	#addi $a0, $a0, 32768
	addi $a0, $a0, 4
	j fillLoop
fillLoopEnd:
	jr $ra
	


mkPlt:	#This function generates a platform at pos (a0, a1), with direction a2, dirtype a3, len a4
	lw $t4, 0($sp)
	addi $sp, $sp, -4
	lw $t3, 0($sp)
	addi $sp, $sp, -4
	lw $t2, 0($sp)
	addi $sp, $sp, -4
	lw $t1, 0($sp)
	addi $sp, $sp, -4
	lw $t0, 0($sp)
	addi $sp, $sp, -4
	
	move $t5, $zero
	sll $t0,$t0,24 #Range should be 0 ~ 256
	sll $t1,$t1,16 #Range should be 0 ~ 256
	sll $t2,$t2,7 #Range should be 0 ~ 1
	sll $t3,$t3,5 #Range should be 0 ~ 4
	sll $t4,$t4,1 #Range should be 0 ~ 16
	
	or $t5, $t0, $t1
	or $t5, $t5, $t2
	or $t5, $t5, $t3
	or $t5, $t5, $t4
	ori $t5, $t5, 1
	move $v0, $t5
	jr $ra

#This set of functions will save positions in $t5
mvPltL:
	move $t5, $ra #Storing caller pos
	
	#Set new left-end to platform
	jal pltTX
	move $t0, $v0
	li $t1, 4
	mult $t0, $t1
	mflo $t0
	add $t4, $t0, $s0
	
	jal pltTY
	move $t1, $v0
	mult $t1, $s3
	mflo $t1
	add $t4, $t4, $t1	
	
	move $t6, $a0
	move $a0, $t4
	li $a1, PLATMV
	li $a2, 4
	mult $a1, $a2
	mflo $a1
	move $a2, $s2
	sub $a0, $a0, $a1 #Offset in left direction
	
	jal fill
	
	
	#Set old right-end to nothing
	move $a0, $t6
	jal pltLen
	move $t1, $v0
	
	li $v0, PLATMV
	mult $t1, $v0
	mflo $t1
	
	li $v0, 4
	mult $t1, $v0
	mflo $t1
	add $t4, $t4, $t1
	
	move $a0, $t4
	li $a1, PLATMV
	li $t3, 4
	mult $a1, $t3
	mflo $a1
	sub $a0, $a0, $a1 #Offset in left direction
	
	sub $a0, $a0, $s0
	
	jal replaceCol
	move $a0, $t6
	
	#Update offset position
	li $t0, 1
	sll $t0, $t0, 12
	sub $a0, $a0, $t0
	move $v0, $a0
	jr $t5

mvPltR:
	move $t5, $ra #Storing caller pos
	
	#Set old left-end to nothing
	jal pltTX
	move $t0, $v0
	li $t1, 4
	mult $t0, $t1
	mflo $t4
	
	jal pltTY
	move $t1, $v0
	mult $t1, $s3
	mflo $t1
	add $t4, $t4, $t1	
	
	move $t6, $a0
	move $a0, $t4
	li $a1, PLATMV
	li $a2, 4
	mult $a1, $a2
	mflo $a1
	
	jal replaceCol
	
	move $a0, $t6
	
	#Set new right-end to platform
	jal pltLen
	move $t1, $v0
	li $v0, PLATMV
	mult $t1, $v0
	mflo $t1
	li $v0, 4
	mult $t1, $v0
	mflo $t1
	
	add $t4, $t4, $s0
	add $t4, $t4, $t1
	
	move $a0, $t4
	li $a1, PLATMV
	li $t3, 4
	mult $a1, $t3
	mflo $a1
	move $a2, $s2
	
	jal fill
	move $a0, $t6
	
	#Update offset position
	li $t0, 1
	sll $t0, $t0, 12
	add $a0, $a0, $t0
	move $v0, $a0
	jr $t5

mvPltD:
	move $t5, $ra
	jal pltTX
	move $t0, $v0
	li $t1, 4
	mult $t0, $t1
	mflo $t4
	
	jal pltTY
	move $t1, $v0
	mult $t1, $s3
	mflo $t1
	add $t4, $t4, $t1	
	
	move $t6, $a0
	li $a1, PLATMV
	li $a2, 4
	mult $a1, $a2
	mflo $a1
	
	jal pltLen
	mult $a1, $v0
	mflo $a1
	
	move $a0, $t4
	jal replaceCol
	move $a0, $t6
	
	li $t1, 1
	sll $t1, $t1, 8
	
	add $a0, $a0, $t1
	move $a1, $s2
	jal drwPlt
	move $a0, $v0	
	jr $t5

mvPltU:
	move $t5, $ra
	jal pltTX
	move $t0, $v0
	li $t1, 4
	mult $t0, $t1
	mflo $t4
	
	jal pltTY
	move $t1, $v0
	mult $t1, $s3
	mflo $t1
	add $t4, $t4, $t1	
	
	move $t6, $a0
	li $a1, PLATMV
	li $a2, 4
	mult $a1, $a2
	mflo $a1
	
	jal pltLen
	mult $a1, $v0
	mflo $a1
	
	move $a0, $t4
	jal replaceCol
	move $a0, $t6
	
	li $t1, 1
	sll $t1, $t1, 8
	
	sub $a0, $a0, $t1
	move $a1, $s2
	jal drwPlt
	move $a0, $v0
	jr $t5

pltDir:	#Return platform movement type
	srl $v0, $a0, 7
	andi $v0, 1 #Mask to remove irrelevant data
	jr $ra

pltMv:	#Return platform movement type
	srl $v0, $a0, 5
	andi $v0, 3 #Mask to remove irrelevant data
	jr $ra	

pltLen:	#Return platform length
	srl $v0, $a0, 1
	andi $v0, 15 #Mask to remove irrelevant data
	jr $ra	

pltFX:	#Return platform x offset
	srl $v0, $a0, 12
	andi $v0, 15 #Mask to remove irrelevant data
	jr $ra
	
pltFY:	#Return platform y offset
	srl $v0, $a0, 8
	andi $v0, 15 #Mask to remove irrelevant data
	jr $ra
	
pltX:	#Return platform start x
	srl $v0, $a0, 24
	jr $ra

pltY:	#Return platform start y	
	srl $v0, $a0, 16
	andi $v0, 255 #Mask to remove irrelevant data
	jr $ra	

pltTX:
	move $t0, $ra
	jal pltX
	move $t1, $v0
	jal pltFX
	li $t2, PLATMV
	mult $v0, $t2
	mflo $v0
	add $v0, $t1, $v0
	jr $t0

pltTY:
	move $t0, $ra
	jal pltY
	move $t1, $v0
	jal pltFY
	li $t2, PLATMV
	mult $v0, $t2
	mflo $v0
	add $v0, $t1, $v0
	jr $t0

getPC:	#Get current pc value
	move $v0, $ra
	jr $ra

QUIT:	li $v0, 10
	syscall
